import React, {useState,useEffect} from 'react'
import axios from 'axios'


// makes requests to all of our rails API endpoint
// Then we can take the response from our api request and use 
//it to update our state to replace our empty airlines array with an array 
//of the actual airline data from our database.

const Airlines = () => {
  const [airlines, setAirlines] = useState([])

  useEffect( () => {
    axios.get('/api/v1/airlines.json')
    .then( resp => {
      setAirlines(resp.data)
    })
    .catch( data => {
      debugger
    })
  }, [])

  const grid = airlines.map((airline, index) => {
    return (<li key={index}>{airline.data.attributes.name}</li>)
  }) //lists all the airline names 


  return (
    <div className="home">
      <div className="header">
        <h1>OpenFlights</h1>
        <p className="subheader">Honest, unbiased airline reviews. Share your experience.</p>
      </div>
      <div className="grid">
        <ul>
          {grid}
        </ul>
      </div>
    </div>
  )
}

export default Airlines
