import React from 'react'
import styled from 'styled-components'

const Card = styled.div`
  border: 1px solid #efefef;
  background: #fff;
`

const AirlineLogo = styled.div`
  height: 50px;
`

const AirlineName = styled.div`
  padding:20px;
`

const LinkWrapper = styled.div`
  margin: 20px 0;
  height:50px;

  a {
    color: #fff;
    background-color: #71b406;
    border-radius: 4px;
    padding: 10px 30px;
    cursor: pointer;
    border-radius: 3px;
    border: 1px solid #71b406;
    text-align: center;
    line-height: 20px;
    min-height: 40px;
    margin: 7px;
    font-weight: 600;
    text-decoration: none;
  }
`

const Airline = (props) => {
    return (
        <div className="card">
            <div className="airline-logo">
                <img src={props.airline.image_url} alt={props.airline.name}/>
        </div>

        <div className="airline-name">{props.airline.name}</div>
        <div className="airline score">{props.airline.avg_score}</div>
        <div className="airline-link">
            <a href={`/airlines/${props.airline.slug}`}>View Airline</a> 
        </div>
        </div>

    )
}

export default Airline
